/**		1era Fase	**/

SELECT COUNT(pro.ID_PROVEEDOR) as 'Cantidad de proovedores', pro.NOMBRE
FROM PROVEEDOR as pro
GROUP BY pro.NOMBRE
ORDER BY pro.NOMBRE ASC
GO

/**		2da Fase	**/

SELECT COUNT(suc.ID_SUCURSAL) 'Sucursales disponibles', COUNT(suc.NOMBRE_SUCURSAL) as 'Sucursales reconocidas', COUNT(suc.ID_PROVEEDOR) as 'Sucursales con proveedor'
FROM SUCURSAL as suc
GO

SELECT COUNT(osc.ID_Proveedor) as 'Proveedores', COUNT(osc.ID_ORDEN_COMPRA) as 'Compras', COUNT(osc.FECHA_ORDEN_COMPRA)
FROM ORDEN_COMPRA as osc
GO

SELECT COUNT(clt.ID_CLIENTE) as 'Clientes', COUNT(clt.RFC) as 'Registrados', COUNT(clt.NOMBRE) 'Nombrados'
FROM CLIENTE as clt
GO

/**		3ra Fase	**/

SELECT COUNT(osc.ID_Proveedor) as 'Proveedores', COUNT(osc.ID_ORDEN_COMPRA) as 'Compras', COUNT(osc.FECHA_ORDEN_COMPRA) as 'Fecha indiferente'
FROM ORDEN_COMPRA as osc
WHERE osc.ID_PROVEEDOR = 'CC2B3352-CE74-45E7-9126-5A3FE62A8270'
GO

SELECT COUNT(clt.ID_CLIENTE) as 'Clientes', COUNT(clt.RFC) as 'Registrados', COUNT(clt.NOMBRE) 'Nombrados'
FROM CLIENTE as clt
WHERE clt.APELLIDOS LIKE '%J%'
GO

/**		4ta Fase	**/

SELECT COUNT(ps.ID_PUESTO) as 'PUESTO', COUNT(ps.SUELDO) 'SUELDO', COUNT(ps.TIPO) 'TIPO'
FROM PUESTO as ps
HAVING COUNT(ps.SUELDO) > 3
GO

SELECT COUNT(clt.ID_CLIENTE) as 'Clientes', COUNT(clt.RFC) as 'Registrados', COUNT(clt.NOMBRE) 'Nombrados'
FROM CLIENTE as clt
HAVING COUNT(clt.ID_CLIENTE) > 10
GO

/**		5ta Fase	**/

SELECT TOP 5 NOMBRE, DIRECCION, TELEFONO
FROM PROVEEDOR
WHERE NOMBRE LIKE '%Farm%'
GO